import { Component } from '@angular/core';
import { categories } from '../categories';

@Component({
  selector: 'app-app-categories',
  templateUrl: './app-categories.component.html',
  styleUrls: ['./app-categories.component.css']
})
export class AppCategoriesComponent {
  categories = categories;
}
<h1>Categories</h1>

<div *ngFor="let category of categories" style="display: inline-block;">
    <div class="category">
        <h3>{{ category.name }}</h3>

        <a>
            <img class="category-image" src="{{ category.image }}" alt="image of the category">
        </a>
    </div>
</div>
