import { Component, Input, OnChanges, SimpleChanges } from '@angular/core';
import { products } from '../products/products.component';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css'],
})
export class ProductListComponent implements OnChanges {
  @Input() list_id: number = -1;

  products = products;
  to_show = false;

  ngOnChanges(changes: SimpleChanges) {
    for (const propName in changes) {
      const chng = changes[propName];
      if (chng.previousValue != chng.currentValue) {
        this.to_show = true;
        con
      }
    }
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }
}

/*
Copyright Google LLC. All Rights Reserved.
Use of this source code is governed by an MIT-style license that
can be found in the LICENSE file at https://angular.io/license
*/
<div *ngIf="to_show">
  <h2>Products</h2>

  <div *ngFor="let product of products">
    <h3>
      <a
        [title]="product.name + ' details'"
        [routerLink]="['/products', product.id]"
      >
        {{ product.name }}
      </a>
    </h3>

    <div>
      <img src="{{ product.image }}" alt="image of the product" />
    </div>

    <p *ngIf="product.description">Description: {{ product.description }}</p>
    <p *ngIf="!product.description">Generated description #{{ product.id }}</p>

    <a href="{{ product.url }}" target="_blank"
      ><button type="button">Kaspi page</button></a
    >

    <app-product-alerts [product]="product" (notify)="onNotify()">
    </app-product-alerts>

  </div>
</div>
